import React, { useState, useEffect, useRef } from 'react';
import ApiService from '../services/api';
import WebSocketService from '../services/websocket';
import './ChatRoom.css';

const ChatRoom = ({ roomData, onLeaveRoom }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [connected, setConnected] = useState(false);
  const messagesEndRef = useRef(null);

  // Initialize WebSocket connection and load messages
  useEffect(() => {
    const initializeChat = async () => {
      try {
        // Connect to WebSocket
        await WebSocketService.connect();
        setConnected(true);
        console.log('WebSocket connected successfully');

        // Subscribe to room messages
        WebSocketService.subscribeToRoom(roomData.roomId, (message) => {
          console.log('Received message:', message);
          const formattedMessage = {
            id: message.id || Date.now(),
            userId: message.userId,
            userName: message.userName,
            message: message.message,
            timestamp: new Date(message.timestamp),
            avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(message.userName)}&background=3b82f6&color=fff`
          };
          setMessages(prev => [...prev, formattedMessage]);
        });

        // Load existing messages from API
        const messagesResponse = await ApiService.getRoomMessages(roomData.roomId);
        if (messagesResponse && messagesResponse.content) {
          const formattedMessages = messagesResponse.content.map(msg => ({
            id: msg.id,
            userId: msg.userId,
            userName: msg.userName,
            message: msg.message,
            timestamp: new Date(msg.timestamp),
            avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(msg.userName)}&background=3b82f6&color=fff`
          }));
          setMessages(formattedMessages);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error initializing chat:', error);
        setLoading(false);
        // Fallback to mock data if API fails
        const initialMessages = [
          {
            id: 1,
            userId: 'user1',
            userName: 'Durgesh Tiwari',
            message: 'how are you??',
            timestamp: new Date(Date.now() - 300000),
            avatar: 'https://ui-avatars.com/api/?name=Durgesh+Tiwari&background=4f46e5&color=fff'
          },
          {
            id: 2,
            userId: 'user2',
            userName: 'Amit Kumar',
            message: 'good.',
            timestamp: new Date(Date.now() - 120000),
            avatar: 'https://ui-avatars.com/api/?name=Amit+Kumar&background=059669&color=fff'
          }
        ];
        setMessages(initialMessages);
      }
    };

    initializeChat();

    // Cleanup on unmount
    return () => {
      WebSocketService.disconnect();
    };
  }, [roomData.roomId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() && connected) {
      const messageRequest = {
        userId: roomData.userId,
        userName: roomData.userName,
        message: newMessage.trim(),
        timestamp: new Date().toISOString()
      };

      // Send message via WebSocket
      const success = WebSocketService.sendMessage(roomData.roomId, messageRequest);

      if (success) {
        setNewMessage('');
      } else {
        console.error('Failed to send message');
        // Optionally show error to user
      }
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="chat-room-container">
      <div className="chat-header">
        <div className="room-info">
          <span className="room-id">Room: {roomData.roomId}</span>
          <span className="user-info">User: {roomData.userName}</span>
          <span className={`connection-status ${connected ? 'connected' : 'disconnected'}`}>
            {connected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
          </span>
        </div>
        <button className="leave-btn" onClick={onLeaveRoom}>
          Leave Room
        </button>
      </div>

      <div className="chat-content">
        <div className="messages-container">
          {loading ? (
            <div className="loading-message" style={{ textAlign: 'center', padding: '20px' }}>
              Loading messages...
            </div>
          ) : (
            messages.map((msg) => (
              <div 
                key={msg.id} 
                className={`message ${msg.userId === roomData.userId ? 'own-message' : 'other-message'}`}
              >
                <div className="message-avatar">
                  <img src={msg.avatar} alt={msg.userName} />
                </div>
                <div className="message-content">
                  <div className="message-header">
                    <span className="message-author">{msg.userName}</span>
                    <span className="message-time">{formatTime(msg.timestamp)}</span>
                  </div>
                  <div className="message-text">{msg.message}</div>
                </div>
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      <div className="message-input-container">
        <form onSubmit={handleSendMessage} className="message-form">
          <input
            type="text"
            placeholder="Type a message..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            className="message-input"
          />
          <button type="submit" className="send-btn" disabled={!newMessage.trim() || !connected}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatRoom;
